html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}

<style>
    .table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: #B7CCF2;
    color:#000000;
    font-size:16px;
    }
    .print-format table, .print-format tr, 
    .print-format td, .print-format div, .print-format p {
        font-family: sans-serif;
        font-size: "12px";
        line-height: 120%;
        vertical-align: middle;
    }
    @media screen {
        .print-format {
            width: 210mm;
            padding:1mm;
            max-height: 297mm;
        }
    }
</style>

<table class="table table-condensed table-hover table-bordered">
  <tr>
    <td style="width:10%">          
        <b>{{ doc.name }} <br> {{ doc.process_sheet }}</b><br>
    </td>
    <td style="width:10%">          
        {{ _("Urgency: ") }} <b style="font-size:18px">{{ doc.priority }}</b><br>
    </td>
    <td style="width:20%">          
        <b>{{ _("Operation: ") }}{{ doc.operation }}</b><br>
    </td>
    <td style="width:5%">          
        <b>{{ _("Machine: ") }}</b><br>
    </td>
    <td style="width:15%">          
        <b>{{ _("") }}</b><br>
    </td>
    <td style="width:5%">          
        <b>{{ _("Operator: ") }}</b><br>
    </td>
    <td style="width:15%">          
        <b>{{ _("") }}</b><br>
    </td>
    <td style="width:5%">          
        <b>{{ _("Qty: ") }}</b><br>
    </td>
    {% if doc.transfer_entry == 1 %}
        <td style="width:15%">          
            <b style="font-size:18px">{{ doc.qty_available }} / {{ doc.total_qty }}</b><br>
        </td>
    {% else %}
        <td style="width:15%">          
            <b style="font-size:18px">{{ doc.for_quantity }}</b><br>
        </td>
    {% endif %}
  </tr>
  <tr>
    <td>          
        <b>{{ _("Finish Size: ") }}</b><br>
    </td>
    <td colspan="8">          
        <b>{{ doc.production_item }}-{{ doc.description }}</b><br>
    </td>
  </tr>
  <tr>
    <td>        
        <b>{{ "Date" }}</b><br>
    </td>
    <td colspan="2">
        <b>{{ "" }}</b><br>
    </td>
    <td>
        <b>{{ "Start Time" }}</b><br>
    </td>
    <td colspan="2">
        <b>{{ "" }}</b><br>
    </td>
    <td>
        <b>{{ "End Time" }}</b><br>
    </td>
    <td colspan="2">
        <b>{{ "" }}</b><br>
    </td>
  </tr>
  {% if doc.allow_consumption_of_rm == 1 %}
  {% for row in doc.rm_consumed %}
  <tr>
    <td>
        <b>{{ "RM Size: "}}</b>
    </td>
    <td colspan="3">
        {{ row.item_code }} : {{ row.description }}
    </td>
    <td colspan="2">
        <b>{{ "CALC: " }} {{ row.calculated_qty }} {{ "& Available: "}} {{ row.qty_available }}</b>
    </td>
    <td>
        <b>{{ "ACTUAL QTY: "}}</b>
    </td>
    <td colspan="2">
        {{ "" }}
    </td>
  </tr>
  {% endfor %}
  {% endif %}


  {% if doc.allow_production_of_wip_materials == 1 %}
  {% for row in doc.item_manufactured %}
  <tr class="text-muted">
    <td> 
        {{ "WIP Size: "}}
    </td>
    <td colspan="3">
        {{ row.item_code }} : {{ row.description }}
    </td>
    <td colspan="2">
        {{ "Available: "}} {{ row.qty_available }}
    </td>
    <td>
        {{ "ACTUAL QTY: "}}
    </td>
    <td colspan="2">
        {{ "" }}
    </td>
  </tr>
  {% endfor %}
  {% endif %}
</table>
<p class="text-muted"; align="right">{{ "Printed On Date: " }}{{ frappe.utils.formatdate(frappe.utils.nowdate(), "dd-MMM-yyyy") }} {{ " & Time: " }} {{ frappe.utils.nowtime() }}</p>
<hr>
